var timing=global.get("timing");

switch (msg.payload) {
    case "u" :  if ((context.selector>4)&&(context.selector<29))
                {
                    timing[((context.days-1)*24)+context.selector-5]++; 
                    if (timing[((context.days-1)*24)+context.selector-5]>26) timing[((context.days-1)*24)+context.selector-5]=26;
                    msg.foryou="Incremented temperature";
                }
                if (context.selector==1) { if (timing[168]<26) {  timing[168]++; msg.foryou="Incremented frost setting 1 degree"; } else msg.foryou="Hit limit"; }
                if (context.selector==2) { if (timing[169]<26) {  timing[169]++; msg.foryou="Incremented away setting 1 degree"; } else msg.foryou="Hit limit"; }
                if (context.selector==29) { if (context.days<7) { context.days++; msg.foryou="Forward one day";} else context.days=1; }
                if (context.selector==30) { if (timing[170]<60) {  timing[170]++; msg.foryou="Incremented humidity setting 1%"; } else msg.foryou="Hit limit"; }

                break;
    case "d" :  if ((context.selector>4)&&(context.selector<29))
                {
                    timing[((context.days-1)*24)+context.selector-5]--; 
                    if (timing[((context.days-1)*24)+context.selector-5]<14) timing[((context.days-1)*24)+context.selector-5]=14;
                    msg.foryou="Decremented temperature";
                }
                if (context.selector==1) { if (timing[168]>8) { timing[168]--; msg.foryou="Decremented frost setting 1 degree"; } else msg.foryou="Hit limit"; }
                if (context.selector==2) { if (timing[169]>8) { timing[169]--; msg.foryou="Decremented away setting 1 degree"; } else msg.foryou="Hit limit"; }
                if (context.selector==29) { if (context.days>1) { context.days--;  msg.foryou="Backward one day";} else context.days = 7;  }
                if (context.selector==30) { if (timing[170]>30) { timing[170]--; msg.foryou="Decremented humidity setting 1%"; } else msg.foryou="Hit limit"; }

                break;
    case 'r' :  if ((context.selector>4)&&(context.selector<28))
                        {
                         timing[((context.days-1)*24)+context.selector-4]=timing[((context.days-1)*24)+context.selector-5];   
                         context.selector++;
                         msg.foryou="Copied settings to the next hour slot";
                        }
                if ((context.selector==29)&&(context.days<7))
                        {
                         for (var a=0;a<24;a++)
                            {
                             timing[((context.days)*24)+a]=timing[((context.days-1)*24)+a];   
                            }
                         context.days++;
                         msg.foryou="Copied a complete day's settings";
                        }
                break;
    case 's': context.saving=0;  msg.foryou="Settings saved to disk"; break;
    case '1':
    case '2':
    case '3':
    case '4':
    case '5':
    case '6':
    case '7':
    case '8':
    case '9':
    case '10':
    case '11':
    case '12':
    case '13':
    case '14':
