    case '21':
    case '22':
    case '23':
    case '24':
    case '25':
    case '26':
    case '27':
    case '28':
    case '29':
    case '30': context.selector=parseInt(msg.payload);
               if (msg.payload=='1') msg.foryou="Frost temperature selection"; 
               else if (msg.payload=='2') msg.foryou="Away temperature selection";
               else if (msg.payload=='29') msg.foryou="Day selection";
               else if (msg.payload=='30') msg.foryou="Humidity selection";
               else  msg.foryou="Selected hour period " + (parseInt(msg.payload)-4);
               break;
    case 'c' : msg.payload="anything";  msg.foryou="Changes cancelled"; node.send([null,null,msg]);
}

msg.temperatures=context.temperatures;
msg.timing=timing;
msg.days=context.days;
msg.selector=context.selector;


msg.myStatus=context.global.myStatus;

node.send([msg,null,null]);

if (context.saving===0) {
    msg.topic="";
    msg.timing="";
    msg.payload=JSON.stringify(timing);
    node.send([null,msg,null]); 
    context.saving=1;
}

msg.foryou="";

var timing=global.get("timing");
var now = new Date();
msg.payload=timing[(now.getDay()*24)+now.getHours()];
msg.frost=timing[168];
msg.away=timing[169];
msg.setHumidity=timing[170];

msg.temperature=global.get("incomingTemperature");
msg.humidity=global.get("incomingHumidity");

msg.set=msg.payload;
if (global.get("manualTimer")) global.set("manualTimer",global.get("manualTimer")-1);
else global.set("manual",0);

msg.desired=msg.set;
global.set("setTemperature",msg.desired);
msg.desired+=global.get("manual");
if (global.get("frost")) { msg.desired=global.get("timing")[168]; global.set("frost",global.get("frost")-1); }

if (global.get("away")) { 
    msg.desired=global.get("timing")[169];
}

if (msg.desired>msg.temperature) msg.heatRelay=1; else msg.heatRelay=0;
if (msg.humidity>msg.setHumidity) msg.humidityRelay=1; else msg.humidityRelay=0;

//context.global.myStatus="Heating: " + ((msg.heatRelay)? "ON":"OFF") + " Dehumidifier: " + ((msg.humidityRelay)? "ON":"OFF");
global.set("myStatus","-");

node.status({fill:"blue",shape:"dot",text:"Set point " + msg.desired + "c - Actual " + msg.temperature +"c"});

if ( typeof msg.value == 'undefined' ) msg.value=global.get("incomingTemperature");

node.send(msg);

var prev=context.get("prev");

if ( msg.heatRelay != prev ) {
    context.set("prev",msg.Relay);
    msg.payload = Number(msg.payload);
    if (msg.heatRelay==1) {
            global.set("heater", 1);
            node.status({fill:"red",shape:"dot",text:"Heat ON"});
            return {payload: 1};
    } else {
            global.set("heater", 0);
            node.status({fill:"black",shape:"dot",text:"Heat OFF"});
            return {payload: 0};
    }

    return msg;
}

// here we set manual override controls - valid for 60 minutes after last change.

if (global.get("away")===0)
{
    if (msg.payload=="u") { global.set("manualTimer",60); if (global.get("manual")<10) global.set("manual",global.get("manual")+1); }
    if (msg.payload=="d") { global.set("manualTimer",60); if (global.get("manual")>-10) global.set("manual",global.get("manual")-1); }
    if (msg.payload=="a") { global.set("manualTimer",0); global.set("manual",0); }
}

if (global.get("manual")===0) msg.colour="#aaddff"; else msg.colour="#aaffaa";

msg.desired= global.get("setTemperature") + global.get("manual");
if (global.get("frost")) { msg.desired=global.get("timing")[168]; msg.colour="#ffbbbb"; }
if (global.get("away"))  { msg.desired=global.get("timing")[169];msg.colour="#ffbbbb";  }
msg.value=msg.desired;

return msg;

// here we set manual override controls - valid for 60 minutes after last change.
var changed=0;

switch (msg.payload) {
    case "u":
        node.log("Antes " + global.get("away"));
        global.set("away", global.get("away")+1); 
        changed=1;
        break;

    case "uu":
        global.set("away", global.get("away")+24);
        changed = 1;
        break;

    case "a" :
        global.set("away",0);
        changed=1;
        break;

    case "reduce":
        if (global.get("away")) { 
            global.set("away", global.get("away")-1); 
            changed=1;
        }

        break;
}

if (changed == 1) {
    msg.away=global.get("away");
    if (global.get("away")===0) msg.colour="#aaddff"; else msg.colour="#aaffaa";

    var t=parseInt(msg.away);
    node.status({text:"horas: " + t});

    if (t>23) {
        t=((t/24).toFixed(0))+"d"+(t%24)+"h"; 
    } else {
        t=t+"h";
    }

    msg.away=t;

    msg.payload="";
    return msg;
}

function theNewDate()
{
   var d = new Date(),
    minutes = d.getMinutes().toString().length == 1 ? '0'+d.getMinutes() : d.getMinutes();
    hours = d.getHours().toString().length == 1 ? '0'+d.getHours() : d.getHours();
    ampm = d.getHours() >= 12 ? 'pm' : 'am';
    months = ['Jan','Feb','Mar','Apr','May','Jun','Jul','Aug','Sep','Oct','Nov','Dec'];
    days = ['Sun','Mon','Tue','Wed','Thu','Fri','Sat'];
    var m= days[d.getDay()]+' '+months[d.getMonth()]+' '+d.getDate()+' '+d.getFullYear()+' '+hours+':'+minutes+ampm;
    return m;
}

msg.topic=theNewDate();
msg.payload="Humidity: " + global.get("incomingHumidity") +"%" ;
return msg;

