if ( (prev != msg.heatRelay) ) {
    var newmsg = {
        "payload": "INSERT",
        "topic": "INSERT INTO dhtreadings" +
        "(temperature, set_temperature, heater_status, currentdate, currenttime, device) " +
        "values(" + msg.temperature + "," + msg.desired + "," + msg.heatRelay + ", date('now'), time('now'), 'DT11')"
    }
    prev = msg.heatRelay;
    return newmsg;
}

